mapper.select:
SELECT F.FUND_CODE AS invtNo ,F.FUND_NAME AS invtName ,F.CURRENCY AS invtExchCurr,( SELECT a.PARAMETER_NAME FROM ESERVICE_ADM.DBO.PARAMETER a ,ESERVICE_ADM.DBO.PARAMETER_CATEGORY b WHERE a.PARAMETER_CATEGORY_ID = b.PARAMETER_CATEGORY_ID AND a.SYSTEM_ID = b.SYSTEM_ID AND a.STATUS = b.STATUS AND b.STATUS = 1 AND b.SYSTEM_ID = AND b.CATEGORY_CODE = AND a.PARAMETER_CODE = F.CURRENCY ) AS currency ,F.FUND_ISSUER_CODE AS company ,FV.EFFECTIVE_DATE effectiveDate ,FV.SELL_PRICE sellPrice, R.UP_VALUE upValue, R.DOWN_VALUE downValue FROM ESERVICE.DBO.FUND F LEFT JOIN (SELECT FUND_CODE, SELL_PRICE, EFFECTIVE_DATE, RANK() OVER (PARTITION BY FUND_CODE ORDER BY EFFECTIVE_DATE DESC, SELL_PRICE DESC) RN FROM FUND_VALUE) FV ON F.FUND_CODE = FV.FUND_CODE AND FV.RN = 1 LEFT JOIN ( SELECT UP_VALUE, DOWN_VALUE, FUND_CODE FROM (SELECT TOP 1 * FROM ESERVICE.DBO.TRANS WHERE STATUS = '2' AND TRANS_TYPE = 'FUND_NOTIFICATION' ORDER BY TRANS_NUM DESC) T INNER JOIN ESERVICE.DBO.TRANS_POLICY TP ON T.TRANS_NUM = TP.TRANS_NUM INNER JOIN ESERVICE.DBO.TRANS_FUND_NOTIFICATION TFN ON TFN.TRANS_NUM = TP.TRANS_NUM INNER JOIN ESERVICE.DBO.TRANS_FUND_NOTIFICATION_DTL TFD ON TFN.ID = TFD.TRANS_FUND_NOTIFICATION_ID WHERE TP.POLICY_NO = #{policyNo} ) R ON F.FUND_CODE = R.FUND_CODE
SUBSTRING(#{policyNo}, 0, 3) = F.PRODUCT_NO AND F.status = '00' and (getdate() between (case when F.START_DATE is null then getdate() else F.START_DATE end) and (case when F.END_DATE is null then getdate() else F.END_DATE end))
AND F.RISK_BENE_LEVEL IN
#{rr}
AND F.FUND_CODE NOT IN
#{funNo}
