mapper.select:
select TRANS_TYPE from ESERVICE.DBO.TRANS
where TRANS_NUM = #{transNum}
order by CREATE_DATE desc

select * from ESERVICE.DBO.TRANS_MEDICAL_TREATMENT_CLAIM_FILEDATAS
where CLAIMS_SEQ_ID = #{claimSeqId}
order by CREATE_DATE desc

mapper.insert:
