mapper.sql:
SELECT ROW_NUMBER() OVER (ORDER BY SADP_TR_DATE) AS ROW_NUM ,POLICY_NO ,SADP_TR_DATE ,SADP_EXCH_DATE ,SADP_TR_TYPE ,case when SADP_TR_CODE in ('INV102','INV103') then 'INV101' else 'INV101' end AS SADP_TR_CODE
,( CASE WHEN SUBSTRING(INVT_NO, 1, 2) = 'RT' THEN SADP_AFEXCH_AMT ELSE SADP_TR_UNITS END ) AS SADP_TR_UNITS ,SADP_AFEXCH_AMT ,INVT_NAME ,INVT_NO ,INVT_EXCH_CURR
,( CASE WHEN SUBSTRING(INVT_NO, 1, 2) = 'RT' THEN 1 ELSE case when SADP_TR_CODE = 'INV201' then ( SELECT CASE WHEN SADP_TR_TYPE = '1' THEN BUY_PRICE WHEN SADP_TR_TYPE = '2' THEN SELL_PRICE END AS NET_VALUE FROM ESERVICE.DBO.FUND_VALUE WHERE convert(varchar, EFFECTIVE_DATE, 111) = convert(varchar, SADP_EXCH_DATE, 111) AND FUND_CODE = INVT_NO) else (SELECT CASE WHEN SADP_TR_TYPE = '1' THEN BUY_PRICE WHEN SADP_TR_TYPE = '2' THEN SELL_PRICE END AS NET_VALUE FROM ESERVICE.DBO.FUND_VALUE WHERE convert(varchar, EFFECTIVE_DATE, 111) = convert(varchar, SADP_TR_DATE, 111) AND FUND_CODE = INVT_NO) end END ) AS NET_VALUE
,( SELECT TOP 1 CASE WHEN SADP_TR_TYPE = '1' THEN SELL_PRICE WHEN SADP_TR_TYPE = '2' THEN BUY_PRICE END AS EXCH_RATE FROM ESERVICE.DBO.EXCHANGE_RATE WHERE convert(varchar, EFFECTIVE_DATE, 111) = convert(varchar, SADP_EXCH_DATE, 111) AND EXCHANGE_CODE = INVT_EXCH_CURR ) AS EXCH_RATE FROM ESERVICE.DBO.FUND_TRANSACTION WHERE POLICY_NO = #{policyNo}
AND SADP_TR_CODE IN ('INV101', 'INV102', 'INV103')
AND SADP_TR_CODE = #{trCode}
AND convert(varchar, SADP_TR_DATE, 111) >= #{startDate}
AND convert(varchar, SADP_TR_DATE, 111) <= #{endDate}

mapper.select:
WITH FUND_TRANSACTION_DATE_TMP AS (
) SELECT (SELECT MAX(ROW_NUM) FROM FUND_TRANSACTION_DATE_TMP) AS TOTAL_ROW ,#{pageSize} AS PAGE_SIZE ,#{pageNum} AS PAGE_NUM , FROM FUND_TRANSACTION_DATE_TMP
WHERE ROW_NUM >= ((#{pageNum} - 1) * #{pageSize}) + 1 AND ROW_NUM <= (#{pageNum} * #{pageSize})
