mapper.select:
WITH FUND_TRANS_DATE_TMP AS ( SELECT ROW_NUMBER() OVER (ORDER BY ts.TRANS_DATE desc) AS ROW_NUM, ts.TRANS_NUM, ts.TRANS_DATE, ts.USER_ID, ( SELECT COUNT(POLICY_NO) FROM ESERVICE.DBO.TRANS_POLICY WHERE TRANS_NUM = ts.TRANS_NUM
AND POLICY_NO = #{policyNo}
) AS POLICY_NO, ( select TCI.FROM_COMPANY_ID FROM_COMPANY_ID FROM ESERVICE.DBO.TRANS_CONTACT_INFO TC JOIN ESERVICE.DBO.TRANS_CONTACT_INFO_DTL TCI ON TC.ID =TCI.TRANS_CONTACT_ID WHERE TC.TRANS_NUM=ts.TRANS_NUM ) FROM_COMPANY_ID , ts.TRANS_TYPE, ts.STATUS, (SELECT COUNT(*) FROM ESERVICE.DBO.TRANS_ENDORSEMENT WHERE TRANS_NUM = ts.TRANS_NUM OR TRANS_NUM IN (SELECT TRANS_NUM_MERGE FROM ESERVICE.DBO.TRANS_MERGE WHERE TRANS_NUM = ts.TRANS_NUM)) ENDORSEMENT_STATUS FROM ESERVICE.DBO.TRANS ts WHERE ts.USER_ID = #{userId}
AND ts.TRANS_TYPE = #{transType}
AND ts.STATUS = #{status}
AND convert(varchar(10), ts.TRANS_DATE, 111) >= #{startDate}
AND convert(varchar(10), ts.TRANS_DATE, 111) <= #{endDate}
) SELECT (SELECT MAX(ROW_NUM) FROM FUND_TRANS_DATE_TMP) AS totalRow, t.FROM_COMPANY_ID, #{pageSize} AS pageSize, #{pageNum} AS pageNum, t.TRANS_NUM AS transNum, t.TRANS_DATE AS transDate, t.USER_ID AS userId, ( SELECT a.PARAMETER_VALUE FROM ESERVICE_ADM.DBO.PARAMETER a JOIN ESERVICE_ADM.DBO.PARAMETER_CATEGORY b on a.PARAMETER_CATEGORY_ID = b.PARAMETER_CATEGORY_ID WHERE 1 = 1 AND a.SYSTEM_ID = b.SYSTEM_ID AND a.STATUS = b.STATUS AND b.STATUS = 1 AND b.SYSTEM_ID = AND b.CATEGORY_CODE = AND a.PARAMETER_CODE = t.TRANS_TYPE ) AS transType, ( SELECT a.PARAMETER_NAME FROM ESERVICE_ADM.DBO.PARAMETER a JOIN ESERVICE_ADM.DBO.PARAMETER_CATEGORY b on a.PARAMETER_CATEGORY_ID = b.PARAMETER_CATEGORY_ID WHERE 1 = 1 AND a.SYSTEM_ID = b.SYSTEM_ID AND a.STATUS = b.STATUS AND b.STATUS = 1 AND b.SYSTEM_ID = AND b.CATEGORY_CODE = AND a.PARAMETER_CODE = t.TRANS_TYPE ) AS transTypeStr, t.STATUS AS status, ( SELECT a.PARAMETER_NAME FROM ESERVICE_ADM.DBO.PARAMETER a JOIN ESERVICE_ADM.DBO.PARAMETER_CATEGORY b on a.PARAMETER_CATEGORY_ID = b.PARAMETER_CATEGORY_ID WHERE 1 = 1 AND a.SYSTEM_ID = b.SYSTEM_ID AND a.STATUS = b.STATUS AND b.STATUS = 1 AND b.SYSTEM_ID = AND b.CATEGORY_CODE = AND a.PARAMETER_CODE = t.STATUS ) AS statusStr, t.ENDORSEMENT_STATUS AS endorsementStatus FROM FUND_TRANS_DATE_TMP t WHERE 1 = 1
AND t.POLICY_NO != 0
AND t.ROW_NUM >= ((#{pageNum} - 1) * #{pageSize}) + 1 AND t.ROW_NUM <= (#{pageNum} * #{pageSize})
