sql.where:
 AND TRANS_NUM = #{transVo.transNum} 
 AND TRANS_DATE = #{transVo.transDate} 
 AND TRANS_TYPE = #{transVo.transType} 
 AND USER_ID = #{transVo.userId} 
 AND STATUS = #{transVo.status} 
 AND CREATE_USER = #{transVo.createUser} 
 AND CREATE_DATE = #{transVo.createDate} 
 AND UPDATE_USER = #{transVo.updateUser} 
 AND UPDATE_DATE = #{transVo.updateDate} 


	AND STATUS IN 

		#{status}




	AND TRANS_NUM IN (
		SELECT 
			tp.TRANS_NUM 
		FROM 
			ESERVICE.DBO.TRANS_POLICY tp 
		WHERE 
			tp.POLICY_NO IN (SELECT LIPM_INSU_NO FROM ESERVICE.DBO.LILIPM WHERE LIPM_ID = #{transVo.rocId})
	)


AND TRANS_DATE > DATEADD(month, -1, getdate()) + 1
