mapper.select:
SELECT * FROM ( SELECT rownum ROW_NUM, lsp.SADP_TR_DATE, lin.INVT_NAME, lin.INVT_NO, lsp.SADP_TR_CODE, CASE WHEN SUBSTR(lsp.SADP_NO, 1, 2) = 'RT' THEN lsp.SADP_TR_AMT ELSE SADP_TR_UNITS END SADP_TR_UNITS, CASE WHEN lsp.SADP_TR_TYPE = '1' THEN leh.EXCH_SPEBK_PURATE WHEN lsp.SADP_TR_TYPE = '2' THEN leh.EXCH_SPEBK_SORATE END FUND_VALUE, lsp.SADP_EXCH_DATE FROM LISADP_ES_FLOWTEST lsp LEFT JOIN LIINVT_ES_FLOWTEST lin ON lsp.SADP_INVT_NO = lin.INVT_NO LEFT JOIN LIEXCH_ES_FLOWTEST leh ON lsp.SADP_TR_DATE = leh.EXCH_DATE
AND lsp.SADP_INSU_NO = #{policyNo}
AND lsp.SADP_TR_CODE IN ('INV101', 'INV102', 'INV103')
AND lsp.SADP_TR_CODE = #{trCode}
AND lsp.SADP_TR_DATE >= to_date(#{startDate}, 'yyyy/MM/dd')
AND lsp.SADP_TR_DATE <= to_date(#{endDate}, 'yyyy/MM/dd')
)
WHERE ROW_NUM >= ((#{pageNum} - 1) * #{pageSize}) + 1 AND ROW_NUM <= (#{pageNum} * #{pageSize})

SELECT COUNT(*) FROM ( SELECT rownum ROW_NUM, lsp.SADP_TR_DATE, lin.INVT_NAME, lin.INVT_NO, lsp.SADP_TR_CODE, CASE WHEN SUBSTR(lsp.SADP_NO, 1, 2) = 'RT' THEN lsp.SADP_TR_AMT ELSE SADP_TR_UNITS END SADP_TR_UNITS, CASE WHEN lsp.SADP_TR_TYPE = '1' THEN leh.EXCH_SPEBK_PURATE WHEN lsp.SADP_TR_TYPE = '2' THEN leh.EXCH_SPEBK_SORATE END FUND_VALUE, lsp.SADP_EXCH_DATE FROM LISADP_ES_FLOWTEST lsp LEFT JOIN LIINVT_ES_FLOWTEST lin ON lsp.SADP_INVT_NO = lin.INVT_NO LEFT JOIN LIEXCH_ES_FLOWTEST leh ON lsp.SADP_TR_DATE = leh.EXCH_DATE
AND lsp.SADP_INSU_NO = #{policyNo}
AND lsp.SADP_TR_CODE IN ('INV101', 'INV102', 'INV103')
AND lsp.SADP_TR_CODE = #{trCode}
AND lsp.SADP_TR_DATE >= to_date(#{startDate}, 'yyyy/MM/dd')
AND lsp.SADP_TR_DATE <= to_date(#{endDate}, 'yyyy/MM/dd')
)

SELECT * FROM ( SELECT rownum ROW_NUM, lpt.PRDT_BOOK_DATE, lpt.PRDT_RCP_AMT, lti.TRHI_INT - lti.TRHI_INT_TAX - lti.TRHI_SUPPLE_PREM INT_AMOUNT, lpt.PRDT_RCP_AMT - lpt.PRDT_TARGET_LOADING - lpt.PRDT_INCRE_LOADING - - (lti.TRHI_INSU_PREM + lti.TRHI_2ND_CHARGE) - lti.TRHI_TR_FEE + ( lti.TRHI_INT - lti.TRHI_INT_TAX - lti.TRHI_SUPPLE_PREM ) PAID_AMOUNT FROM LIPRDT_ES_FLOWTEST lpt LEFT JOIN LITRHI_ES_FLOWTEST lti ON lpt.PRDT_INSU_NO = lti.TRHI_INSU_NO AND lti.TRHI_DATE = lpt.PRDT_BOOK_DATE
AND lpt.PRDT_INSU_NO = #{policyNo}
AND lpt.PRDT_BOOK_DATE >= to_date(#{startDate}, 'yyyy/MM/dd')
AND lpt.PRDT_BOOK_DATE <= to_date(#{endDate}, 'yyyy/MM/dd')
)
WHERE ROW_NUM >= ((#{pageNum} - 1) * #{pageSize}) + 1 AND ROW_NUM <= (#{pageNum} * #{pageSize})

SELECT COUNT(*) FROM ( SELECT rownum ROW_NUM, lpt.PRDT_BOOK_DATE, lpt.PRDT_RCP_AMT, lti.TRHI_INT - lti.TRHI_INT_TAX - lti.TRHI_SUPPLE_PREM INT_AMOUNT, lpt.PRDT_RCP_AMT - lpt.PRDT_TARGET_LOADING - lpt.PRDT_INCRE_LOADING - - (lti.TRHI_INSU_PREM + lti.TRHI_2ND_CHARGE) - lti.TRHI_TR_FEE + ( lti.TRHI_INT - lti.TRHI_INT_TAX - lti.TRHI_SUPPLE_PREM ) PAID_AMOUNT FROM LIPRDT_ES_FLOWTEST lpt LEFT JOIN LITRHI_ES_FLOWTEST lti ON lpt.PRDT_INSU_NO = lti.TRHI_INSU_NO
AND lpt.PRDT_INSU_NO = #{policyNo}
AND lpt.PRDT_BOOK_DATE >= to_date(#{startDate}, 'yyyy/MM/dd')
AND lpt.PRDT_BOOK_DATE <= to_date(#{endDate}, 'yyyy/MM/dd')
)

SELECT INPR_TRAN_DATE effectiveDate, INPR_UNIT_PURPRICE buyPrice, INPR_UNIT_SOLPRICE sellPrice
FROM LIINPR_ES_FLOWTEST

     AND INPR_TRAN_DATE = #{exchangeRateVo.effectiveDate} 
     AND INPR_INVT_NO = #{exchangeRateVo.exchangeCode} 

        AND INPR_TRAN_DATE > DATEADD(month, -1, getdate()) + 1


        AND INPR_TRAN_DATE > DATEADD(month, -3, getdate()) + 1


        AND convert(varchar(7), EFFECTIVE_DATE, 126) = #{exchangeRateVo.queryMonth}
