mapper.select:
SELECT *
FROM (
SELECT
ROW_NUMBER() OVER (ORDER BY CREATE_DATE) AS ROW_NUM
,A.*
FROM (
	SELECT

	FROM
		ESERVICE_JD.DBO.MESSAGE
		WHERE USER_ID = #{userId}
		AND NOTIFY_TIME <= SYSDATETIME()
) A
) B
WHERE
B.ROW_NUM <= #{vo.pageNum} * #{vo.pageSize}
AND B.ROW_NUM > (#{vo.pageNum} - 1) * #{vo.pageSize}
