mapper.sql:
WHERE USER_ID = #{userId} AND NOTIFY_TIME <= SYSDATETIME()

	AND NOTIFY_TIME between #{vo.notifySearchTime} and DATEADD(day, 1, #{vo.notifySearchTime})


	AND STATUS = #{vo.status}

mapper.select:
SELECT * FROM ( SELECT ROW_NUMBER() OVER (ORDER BY CREATE_DATE) AS ROW_NUM ,A.* FROM ( SELECT
FROM ESERVICE_JD.DBO.MESSAGE
) A ) B WHERE B.ROW_NUM <= #{vo.pageNum} * #{vo.pageSize} AND B.ROW_NUM > (#{vo.pageNum} - 1) * #{vo.pageSize}

SELECT COUNT(1) AS AMOUNT FROM ( SELECT
FROM ESERVICE_JD.DBO.MESSAGE
) t
