mapper.update:
update ESERVICE_JD.DBO.USERS set last_chang_password_date = getdate(), LOGIN_FAIL_COUNT = 0, LOGIN_TIME = SYSDATETIME()
, STATUS = #{enable}
where lower(user_id) = lower(#{account})

mapper.select:
SELECT ue.ID user_id, ESERVICE_JD.DBO.FN_DEBASE64(u.email) email, UD.BRANCH_ID FROM ESERVICE_JD.DBO.USERS u INNER JOIN ESERVICE_ADM.DBO.USER_ENTITY UE ON UE.USERNAME = U.USER_ID COLLATE database_default AND UE.REALM_ID = 'elife_jdzq' INNER JOIN ESERVICE_JD.DBO.USER_DEPARTMENT UD ON UE.ID = UD.USER_ID INNER JOIN ESERVICE_JD.DBO.DEPARTMENT D ON UD.DEP_ID = D.DEP_ID WHERE u.LOGIN_SIZE = #{saleId}
AND D.BRANCH_ID = concat(#{agentCode}, #{agentBranchM})
AND D.BRANCH_ID = #{agentCode}

SELECT ue.ID user_id, ESERVICE_JD.DBO.FN_DEBASE64(u.email) email FROM ESERVICE_JD.DBO.USERS u INNER JOIN ESERVICE_ADM.DBO.USER_ENTITY UE ON UE.USERNAME = U.USER_ID COLLATE database_default AND UE.REALM_ID = 'elife_jdzq' INNER JOIN ESERVICE_JD.DBO.USER_DEPARTMENT UD ON UE.ID = UD.USER_ID INNER JOIN ESERVICE_JD.DBO.DEPARTMENT D ON UD.DEP_ID = D.DEP_ID INNER JOIN ESERVICE_ADM.DBO.USER_ROLE UR ON UR.USER_ID = UE.ID INNER JOIN ESERVICE_ADM.DBO.ROLE R ON UR.ROLE_ID = R.ROLE_ID
AND D.BRANCH_ID = concat(#{agentCode}, #{agentBranchM})
AND D.BRANCH_ID = #{agentCode}
AND UD.BRANCH_ID = #{branchCode}
